
public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char sy = '+'; 
		 int a = 20, b = 10; 
		 int c = 0; 
		  
		 if(sy == '+'){ 
		  c = a + b; 
		 }else if(sy == '-'){ 
		  c = a - b; 
		 }else if(sy == '*'){ 
		  c = a * b; 
		 }else if(sy == '/'){ 
		  c = a / b; 
		 }else if(sy == '%'){ 
		  c = a % b; 
		 } 
		 System.out.println("The result is "+ c); 

	}

}